import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import { CardColumns, CardDeck, Container, Jumbotron } from "react-bootstrap";
import { Card } from "react-bootstrap";
import { Button } from "react-bootstrap";

class Header extends Component {
  constructor(props) {
      super(props);
      this.state = {
          contact : '*********'
      }
  }

  updateContact = () => {
      this.setState(state =>  {
          contact: '674128573'
      });
  }

  render() {
    return (
      <Jumbotron>
        <Card style={{ width: "15rem" }}>
          <Card.Img variant="top" src='https://store.storeimages.cdn-apple.com/4982/as-images.apple.com/is/iphone11-select-2019-family?wid=882&amp;hei=1058&amp;fmt=jpeg&amp;qlt=80&amp;op_usm=0.5,0.5&amp;.v=1567022175704'  alt="img" />
          <Card.Body>
            <Card.Title>Iphone 7s</Card.Title>
            <Card.Text>
                <p>Uploaded by Daniel Tano</p>
            </Card.Text>
            <Button variant='info'>
                {this.state.contact}
            </Button>
          </Card.Body>
        </Card>
      </Jumbotron>
    );
  }
}

export default Header;
